sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strCheck = '5a000ebe93e1aa50959b7fda2bba10cd';
	
	var grAppCodeSanity = x_snc_devtools.GetAppRecord('x_snc_codesanity');
	test.TestCase_InstanceScanCheck(strCheck,'sys_app',grAppCodeSanity.sys_id,false);

	var grApp = new GlideRecord('sys_app');
	grApp.initialize();
	grApp.scope = 'global';
	grApp.name = 'TEST GLOBAL';
	var strAppSysId = x_snc_devtools.GlideRecordInsert(grApp);
	test.TestCase_InstanceScanCheck(strCheck,'sys_app',strAppSysId,false);
	
	var grScriptInclude = new GlideRecord('sys_script_include');
	grScriptInclude.initialize();
	grScriptInclude.name = 'TestGlobal';
	grScriptInclude.sys_scope = strAppSysId;
	var strScriptIncludeSysId = x_snc_devtools.GlideRecordInsert(grScriptInclude);
	x_snc_devtools.RecordSetValue('sys_script_include',strScriptIncludeSysId,'sys_created_by','glide.maint',true);
	test.Log(x_snc_devtools.GetRecord('sys_script_include',strScriptIncludeSysId));
	test.TestCase_InstanceScanCheck(strCheck,'sys_app',strAppSysId,true);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
