sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = '2ed0c523db7e91106a03dad1f39619e1';
	
	
	var testdata = new x_snc_devtools.TestDataAPI('_ATF_CODESANITY_');

	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test1',
		'function _ATF_CODESANITY_Test1(param1)\n'
		+'{ gs.info(param1); }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test2',
		'//Just an innocent comment...\n'
		+'function _ATF_CODESANITY_Test2(param1)\n'
		+'{ gs.info(param1); }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);	
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test3',
		'function _ATF_CODESANITY_Test3(param1)\n'
		+'{ gs.info("Just an innocent log output..."); }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);		
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test10',
		'function _ATF_CODESANITY_Test10()'
		+'{'
		+'   function Sum(x, y, z) { return x + y + z; }'
		+'   const aNumbers = [1, 2, 3];'
		+'   gs.info((sum(...numbers));'
		+'}');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);
	
	var grRecord = new GlideRecord('sys_ui_script');
	grRecord.initialize();
	grRecord.sys_scope = 'global';
	grRecord.name = ' _ATF_CODESANITY_Test11';
	grRecord.script = 'function _ATF_CODESANITY_Test11()\n'
		+'{'
		+'   function Sum(x, y, z) { return x + y + z; }'
		+'   const aNumbers = [1, 2, 3];'
		+'   console.log((sum(...numbers));'
		+'}';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.TestCase_InstanceScanCheck(strCheck,'sys_ui_script',strRecordSysId,true);
	

	test.Log(x_snc_devtools.RenderValue(testdata.GetAllData()));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
