sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var GetRecord = x_snc_devtools.GetRecord;
	
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_GetExecutionRulesForCheckByRules(grCheck, rules, vExpected)
	{
		function TestCase_GetExecutionRulesForCheckByRules_CheckResult(vInput,vExpected,vResult)
		{
			return x_snc_devtools.IsEqual(vExpected,vResult);
		}
		
		test.TestCase(
			'Function GetExecutionRulesForCheckByRules()',
			GetExecutionRulesForCheckByRules,
			{
				'Check': grCheck,
				'Rules': rules
			},
			vExpected,
			TestCase_GetExecutionRulesForCheckByRules_CheckResult,
			true);
	}


	TestCase_GetExecutionRulesForCheckByRules(null,null,{});
	
	// CodeSanity - Sys IDs in scripts
	var grCheckSysIdsInScript = GetRecord('scan_check','9dda4d97dbce8510bcced03cd3961921'); 
	
	// CodeSanity - Throwing exceptions
	var grCheckThrowingExceptions = GetRecord('scan_check','6bd82ff8db12cd10b652edb305961910'); 
	
	// No rules, no exceptions
	var rules = {};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,    rules, {});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules, {});

	
	// A single check should not be executed at all
	var rules = {
		blacklist: [
			{
				checks: ['CodeSanity - Sys IDs in scripts']
			}
		]
	};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,    rules,
		{
			blacklist:
			{
				scopes: []
			}
		});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules,
		{});


	// A single check should not be executed if the record is in scope x_snc_codesanity
	var rules = {
		blacklist: [
			{
				scopes: ['x_snc_codesanity'],
				checks: ['CodeSanity - Sys IDs in scripts']
			}
		]
	};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,rules,
		{
			blacklist: 
			{
				scopes: ['x_snc_codesanity']
			}
		});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules,
		{});

	
	// A single check should only be executed if in scope x_snc_codesanity
	var rules = {
		whitelist: [
			{
				scopes: ['x_snc_devtools'],
				checks: ['CodeSanity - Sys IDs in scripts']
			}
		]
	};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,rules,
		{
			whitelist:
			{
				scopes: ['x_snc_devtools']
			}
		});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules,{});

	
	
	
	// One check should only be executed if in scope x_snc_codesanity
	// Another check should NOT run if in scope x_snc_codesanity
	var rules = {
		blacklist: [
			{
				scopes: ['x_snc_codesanity'],
				checks: ['CodeSanity - Throwing exceptions']
			}
		],	
		whitelist: [
			{
				scopes: ['x_snc_devtools'],
				checks: ['CodeSanity - Sys IDs in scripts']
			}
		]
	};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,    rules,
		{
			whitelist:
			{
				scopes: ['x_snc_devtools']
			}
		});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules,
		{
			blacklist:
			{
				scopes: ['x_snc_codesanity']
			}
		});

		
	
	// Multiple blacklist rules:
	// One check should NOT run  in scope x_snc_devtools
	// Another check should NOT run if in scope x_snc_codesanity
	var rules = {
		blacklist: [
			{
				scopes: ['x_snc_codesanity'],
				checks: ['CodeSanity - Throwing exceptions']
			},
			{
				scopes: ['x_snc_devtools'],
				checks: ['CodeSanity - Sys IDs in scripts']
			}
		]
	};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,    rules,
		{
			blacklist:
			{
				scopes: ['x_snc_devtools']
			}
		});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules,
		{
			blacklist:
			{
				scopes: ['x_snc_codesanity']
			}
		});
	
	
	// Multiple (seemingly) contradicting rules (whitelists ovveride blacklists)
	// One check is blacklisted for scope x_snc_codesanity
	// The same check is whitelisted for scope x_snc_codesanity
	var rules = {
		blacklist: [
			{
				scopes: ['x_snc_codesanity'],
				checks: ['CodeSanity - Throwing exceptions']
			}
		],	
		whitelist: [
			{
				scopes: ['x_snc_codesanity'],
				checks: ['CodeSanity - Throwing exceptions']
			}
		]
	};
	TestCase_GetExecutionRulesForCheckByRules(grCheckSysIdsInScript,    rules, {});
	TestCase_GetExecutionRulesForCheckByRules(grCheckThrowingExceptions,rules,
		{
			whitelist:
			{
				scopes: ['x_snc_codesanity']
			}
		});

	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
