	// Special case here, because we need to create a script include WITHOUT a name - which is not possible with TestDataAPI
	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grSI);
	test.Log(x_snc_devtools.RenderValue(grSI));
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);
*/

	// Special case here, because we need to create a script include with a short name - which again is not possible with TestDataAPI
	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.name = 'Ab';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grSI);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);
	
	// Special case here, because we need to create an invalid script include name - which again is not possible with TestDataAPI
	var grSI = new GlideRecord('sys_script_include');
	grSI.initialize();
	grSI.name = '1Abcdef';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grSI);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
